body {
    margin: 0;
    font-family: Arial, sans-serif;
}

/* Navbar styles */
.navbar {
    background-color: #4db6ac;
    padding: 20px 10px;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
    width: 100%; /* Fixed width for the navbar */
    height: 100%; /* Full viewport height */
    position: fixed;
    top: 0;
    left: 0;
    overflow-y: auto; /* Enable vertical scrolling */
    display: flex;
    flex-direction: column;
}

/* Navbar menu styles */
.navbar-menu {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: column;
    width: 100%;
    flex-grow: 1; /* Ensures the menu takes up all available space */
    justify-content: space-between; /* Distribute items evenly from top to bottom */
}

/* Navbar item styles */
.nav-item {
    color: white;
    text-decoration: none;
    padding: 15px 20px;
    display: block;
    width: 100%;
    border-radius: 5px;
    transition: background-color 0.3s;
}

.nav-item:hover {
    background-color: #198754; /* Adjust color as needed */
    cursor: pointer;
}

/* Main content */
.main-content {
    margin-left: 250px; /* Offset by the navbar width */
    padding: 20px;
}

/* Optional: Customize scrollbar appearance */
.navbar::-webkit-scrollbar {
    width: 8px;
}

.navbar::-webkit-scrollbar-thumb {
    background-color: #198754;
    border-radius: 10px;
}

.navbar::-webkit-scrollbar-track {
    background-color: #4db6ac;
}

/* Media Queries for Responsive Design */
@media (max-width: 1200px) {
    .navbar {
        width: 220px;
    }
    .main-content {
        margin-left: 220px;
    }
}

@media (max-width: 992px) {
    .navbar {
        width: 200px;
    }
    .main-content {
        margin-left: 200px;
    }
}

@media (max-width: 768px) {
    .navbar {
        width: 180px;
    }
    .main-content {
        margin-left: 180px;
    }
}

@media (max-width: 576px) {
    .navbar {
        width: 150px;
    }
    .main-content {
        margin-left: 150px;
    }
}

