self.onInit = function() {
    // Function to disable the back button
    function disableBackButton() {
        // Push a new state to the history stack to prevent going back
        window.history.pushState(null, null, window.location.href);

        // Listen for popstate events and push the state again to prevent navigation
        window.addEventListener('popstate', function() {
            window.history.pushState(null, null, window.location.href);
        });
    }

    // Function to clear session data and reload the page
    function login() {
        // Clear all session storage data
        sessionStorage.clear();

        // Clear all local storage data
        localStorage.clear();

        // Clear cookies (auth-related)
        document.cookie.split(";").forEach(function(c) {
            document.cookie = c.trim().split("=")[0] + "=;expires=Thu, 01 Jan 1970 00:00:00 UTC;path=/";
        });

        // Redirect to the login page with cache-busting parameter
        window.location.href = window.location.href.split('?')[0] + '?_' + new Date().getTime();
    }

    // Initialize state controller
    let stateController = self.ctx.$scope.ctx.stateController;
    if (stateController) {
        self.ctx.$scope.navToState = function() {
            stateController.navigatePrevState(1);
        }
    }
    
    // Function to open dashboard or perform actions
    self.ctx.$scope.openDashboard = function(actionName) {
        let params = self.ctx.$scope.ctx.stateController.getStateParams();
        let actionEventDescriptors = self.ctx.$scope.ctx.actionsApi.getActionDescriptors('elementClick');
        let actionEvent;

        if (actionEventDescriptors.length) {	
            actionEventDescriptors.forEach(action => {
                if (action.name == actionName) {
                    actionEvent = action;
                }
            });
                
            setTimeout(() => {
                self.ctx.$scope.ctx.actionsApi.handleWidgetAction(null, actionEvent, null, null);	
            }, 0);	
        }
    }

    // Logout function to clear session data and redirect
    self.ctx.$scope.logout = function() {
        // Clear all session storage data
        sessionStorage.clear();

        // Clear all local storage data
        localStorage.clear();

        // Clear cookies (auth-related)
        document.cookie.split(";").forEach(function(c) {
            document.cookie = c.trim().split("=")[0] + "=;expires=Thu, 01 Jan 1970 00:00:00 UTC;path=/";
        });

        // Redirect to the login page with cache-busting parameter
        window.location.replace('https://www.dexterhms.com/login?_=' + new Date().getTime());
    }

    // Call disableBackButton function immediately on page load
    disableBackButton();

    // Regularly reapply back button disabling
    // setInterval(disableBackButton, 50);

    // Prevent navigation using keyboard shortcuts
    window.addEventListener('keydown', function(e) {
        if (e.key === 'Backspace' || e.key === 'ArrowLeft' || (e.ctrlKey && e.key === 'H')) {
            e.preventDefault();
        }
    });
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onDestroy = function() {
}

